if (inherits(dm$SEX, "labelled")) {
  dm &lt;- dm |&gt; mutate(SEX = to_factor(SEX))
}

5.3 Common Cleaning

dm &lt;- dm |&gt;
  mutate(
    AGEGR1 = cut(AGE, breaks=c(-Inf, 50, 65, Inf),
                 labels=c("&lt;50","50-65", "65+"))
  )

12.2 1) Read (or Synthesize) SDTM

library(haven); library(dplyr); library(lubridate)


